import textFieldRules from "../utils/textFieldRules.js";
import setting from "./setting";
export default {
  mixins: [textFieldRules],
  component: {
    setting: setting
  },
  data: function data() {
    return {
      drawer: true,
      showDetail: false,
      perPage: 20,
      pages: 0,
      searchText: "",
      filterData: [],
      headers: [{
        text: "#",
        align: "left",
        sortable: false,
        width: "80px",
        value: "id"
      }, {
        text: "Domain Name",
        align: "left",
        sortable: true,
        value: "name"
      }, {
        text: "CName",
        align: "left",
        sortable: true,
        value: "cname"
      }, {
        text: "Actions",
        align: "left",
        value: "action",
        width: "200px"
      }],
      pagination: {
        rowsPerPage: 20
      },
      rowsPerPageItems: [{
        name: "20 per page",
        value: 20
      }, {
        name: "50 per pages",
        value: 50
      }],
      rawData: [],
      dialog: {
        edit: false
      },
      error: {
        status: false,
        message: ""
      },
      domain: {},
      editedIndex: -1,
      // groupData: [],
      operatorAuth: 0 // userGroups: []

    };
  },
  methods: {
    getAllDomains: function getAllDomains() {
      this.$store.dispatch("global/startLoading");
      this.$store.dispatch("domains/getAllDomains").then(function (result) {
        // console.log(result.data.domain);
        this.filterData = result.data.domains; // this.handleData();

        this.setPages();
        this.$store.dispatch("global/finishLoading");
      }.bind(this)).catch(function (error) {
        this.$store.dispatch("global/finishLoading");
        this.$store.dispatch("global/showSnackbarError", error.message);
      }.bind(this));
    },
    // filterDataAction: function() {
    //     if (this.searchText != "") {
    //         this.filterData = this.filterData.filter(row => {
    //             return (
    //                 !this.searchText ||
    //                 row.name
    //                     .toLowerCase()
    //                     .indexOf(this.searchText.toLowerCase()) > -1 ||
    //                 row.cname
    //                     .toLowerCase()
    //                     .indexOf(this.searchText.toLowerCase()) > -1
    //             );
    //         });
    //     } else {
    //         this.filterData = this.filterData;
    //     }
    //     this.setPages();
    // },
    editItem: function editItem(item, type) {
      this.editedIndex = this.filterData.indexOf(item);
      this.domain = Object.assign({}, item);

      if (type == 0) {
        this.dialog.edit = true;
      }
    },
    updateDomain: function updateDomain() {
      if (this.$refs.editForm.validate()) {
        this.$store.dispatch("global/startLoading");

        if (this.editedIndex == -1) {
          this.addNewDomain();
        } else {
          this.$store.dispatch("global/startLoading");
          this.$store.dispatch("domains/updateDomain", this.domain).then(function (result) {
            this.$store.dispatch("global/finishLoading");
            this.getAllDomains();
            this.closeEditDialog();
          }.bind(this)).catch(function (error) {
            this.$store.dispatch("global/finishLoading");
            this.$store.dispatch("global/showSnackbarError", error.message);
          }.bind(this));
        }
      }
    },
    addItem: function addItem() {
      this.$refs.editForm.reset();
      this.editedIndex = -1;
      this.dialog.edit = true;
    },
    addNewDomain: function addNewDomain() {
      this.domain.user_group_id = this.$store.getters["account/accountGroupId"](); // console.log(this.domain);

      var vm = this;

      if (this.$refs.editForm.validate()) {
        this.$store.dispatch("global/startLoading");
        this.$store.dispatch("domains/newDomain", this.domain).then(function (result) {
          this.$store.dispatch("global/showSnackbarSuccess", "Update user success!");
          this.getAllDomains();
          this.$store.dispatch("global/finishLoading");
        }.bind(this)).catch(function (error) {
          this.$store.dispatch("global/showSnackbarError", error.message);
          this.$store.dispatch("global/finishLoading");
        }.bind(this));
        this.closeEditDialog();
      }
    },
    closeEditDialog: function closeEditDialog() {
      this.dialog.edit = false;
    },
    setPages: function setPages() {
      if (this.perPage == null || this.filterData == null) {
        this.pages = null;
      } else {
        this.pages = Math.ceil(this.filterData.length / this.perPage);
      }
    }
  },
  computed: {
    formTitle: function formTitle() {
      return this.editedIndex === -1 ? "New Domain" : "Edit Domain";
    }
  },
  watch: {
    perPage: function perPage(value) {
      this.pagination.rowsPerPage = value;
      this.setPages();
    }
  },
  mounted: function mounted() {
    this.$router.push("admin/domains");
    this.getAllDomains();
  },
  created: function created() {
    this.getAllDomains(); // console.log("xx");
  }
};
