#user-table
    v-card
        v-card-title
            v-layout(row align-center)
                //- v-flex.px-2(xs12 sm6 md2)
                //-     v-select.pt-0.mt-0(v-model="perPage" :items="rowsPerPageItems" item-text="name" item-value="value" hide-details)
                v-flex.px-2(xs12 sm6 md3)
                    v-text-field.pt-0.mt-0(v-model="searchText" append-icon="search" label="Search" single-line hide-details)
                v-spacer
                v-btn(color="blue-grey lighten-2" dark @click="addItem" :disabled="!status") Add User
        v-data-table.elevation-1(:headers="headers" :items="filterData" :search="searchText" :loading="$store.state.global.isLoading" :pagination.sync="pagination"  hide-actions )
            template(slot="items" slot-scope="props" color="blue-grey lighten-2")
                tr(:class="props.item.status? '': 'grey--text'")
                    td.text-xs-left() {{props.index +1}}
                    td.text-xs-left() 
                        .name.font-weight-medium {{props.item.name}}
                        .email.grey--text.text--darken-3 {{props.item.email}}
                    td.text-xs-left()  {{(props.item.user_type == "origin")? 'root': props.item.user_type}}
                    td.text-xs-left() {{props.item.last_login_time}}
                    td.text-xs-left() {{(props.item.status)? 'acive' : 'inactive'}}
                    td.text-xs-left()
                        v-btn.ma-0(flat icon small color="blue-grey lighten-2" @click="editItem(props.item, 0)" title="edit user" :disabled="!props.item.status" v-if="props.item.user_type != 'origin'")
                            v-icon(small) edit
                        //- v-btn.ma-0(flat icon small color="blue-grey lighten-2" @click="editItem(props.item, 1)" title="change password" :disabled="!props.item.status" v-if="props.item.user_type != 'origin'")
                        //-     v-icon(small) lock
                        v-menu(offset-y left v-if="props.item.user_type != 'origin'") 
                            template(v-slot:activator="{on}")
                                v-btn.ma-0(flat icon small color="blue-grey lighten-2" v-on="on" :disabled="!status")
                                    v-icon( small) more_vert
                            v-list.pa-0
                                v-list-tile(v-if="props.item.status" @click="updateUserStatus(props.item, 0)")
                                    v-list-tile-title inactive user
                                v-list-tile(v-else @click="updateUserStatus(props.item, 1)")
                                    v-list-tile-title active user
                                v-list-tile(@click="forgotPassword(props.item)")
                                    v-list-tile-title reset password
        v-layout.px-2(row align-center)
            v-flex.text-xs-left.py-3(xs4)
                //- b.total-items total: {{filterData.length}} 
            v-flex.text-xs-right.py-3(xs8)
                v-pagination(v-model="pagination.page" :length="pages" :total-visible="7" color="blue-grey lighten-2")
    //- Dialogs
    v-dialog.edit-dialog(v-model="dialog.edit" max-width="460" persistent)
        v-card
            v-card-title.title {{formTitle}}
            v-card-text
                v-form(ref="editForm")
                    v-text-field(v-model="userInfo.name" label="Name" type="text" name="name" :rules="[rules.required]")
                    .role-select(v-if="user_type == 'hiero7'")
                        v-select(v-model="userInfo.user_type" :items="roleItem" item-text="name" item-value="value" label="Role" :rules="[rules.required]")

                    .role-select(v-else)
                        v-select(v-if="userInfo.user_type !== 'origin'" v-model="userInfo.user_type" :items="roleItems" item-text="name" item-value="value" label="Role" :rules="[rules.required]" ) 
                        v-text-field(v-else label="Role" type="text" name="role" disabled value="Admin") 
                        //- .role-select
                        //-     v-select(v-if="userInfo.user_type != 'origin'" v-model="userInfo.user_type" :items="roleItems" item-text="name" item-value="value" label="Role" :rules="[rules.required]" )
                        //-     v-text-field(v-else label="Role" type="text" name="role" disabled value="Admin") 

                    v-text-field(v-model="userInfo.email" label="Email" type="text" name="email" :rules="[rules.required, rules.email]" :disabled="editedIndex === -1 ? false : true")
                    v-text-field(v-model="userInfo.phone" label="Phone" type="text" name="phone")
                    v-text-field(v-model="userInfo.password" label="Password" type="password" name="password")
                    //- v-text-field(v-if="editedIndex===-1" v-model="userInfo.password" label="Password" type="password" name="password" :rules="[rules.required, rules.min]" )

                    //- v-select(v-if="userInfo.user_type == 'user' && editedIndex != -1" v-model="userInfo.products" :items="products" item-text="name" item-value="id" label="Select Products" multiple chips hint="permission" persistent-hint return-object)
                    v-alert.text-md-left(:value="error.status" color="error" icon="warning" outline transition="scale-transition") {{error.message}}

            v-card-actions  
                v-spacer
                v-btn(color="grey" flat="flat" @click="closeEditDialog") Cancel
                v-btn(color="blue-grey lighten-2" flat="flat" @click="updateUser") Save

