import textFieldRules from "../utils/textFieldRules.js";
import userGroupDetail from "./UsersGroup";

export default {
    mixins: [textFieldRules],

    component: {
        userGroupDetail
    },
    data() {
        return {
            drawer: true,
            showDetail: false,
            perPage: 20,
            pages: 0,
            searchText: "",
            filterData: [],
            headers: [
                {
                    text: "#",
                    align: "left",
                    sortable: false,
                    width: "80px",
                    value: "id"
                },
                {
                    text: "User Group",
                    align: "left",
                    sortable: true,
                    value: "name"
                },
                {
                    text: "Sub-Accounts",
                    align: "left",
                    sortable: true,
                    value: "subAccounts"
                },
                {
                    text: "Status",
                    align: "left",
                    // width: "100px",
                    value: "status",
                    sortable: true
                },
                {
                    text: "Actions",
                    align: "left",
                    value: "action",
                    width: "200px",
                    sortable: true
                }
            ],
            pagination: {
                rowsPerPage: 20
            },
            rowsPerPageItems: [
                { name: "20 per page", value: 20 },
                { name: "50 per pages", value: 50 }
            ],
            rawData: [],
            dialog: {
                edit: false
            },
            error: {
                status: false,
                message: ""
            },
            userInfo: {},
            editedIndex: -1,
            groupData: [],
            operatorAuth: 0,
            userGroups: []
        };
    },
    watch: {
        searchText: {
            deep: true,
            handler: function() {
                this.filterDataAction();
            }
        }
    },
    methods: {
        getAllUsers: function() {
            this.$store.dispatch("global/startLoading");
            this.$store
                .dispatch("users/getAll")
                .then(
                    function(result) {
                        this.rawData = result.data;
                        this.rawData.forEach((o, i) => {
                            this.userGroups.forEach((obj, idx) => {
                                if (o.user_group_id == obj.id) {
                                    o["user_group_name"] = obj.name;
                                }
                            });
                        });
                        this.handleData();
                        this.setPages();
                        this.$store.dispatch("global/finishLoading");
                    }.bind(this)
                )
                .catch(
                    function(error) {
                        this.$store.dispatch("global/finishLoading");
                        this.$store.dispatch(
                            "global/showSnackbarError",
                            error.message
                        );
                    }.bind(this)
                );
        },
        getAllUserGroups: function() {
            this.$store.dispatch("global/startLoading");
            this.$store
                .dispatch("users/getAllUserGroups")
                .then(
                    function(result) {
                        this.userGroups = result.data;
                        this.getAllUsers();
                        this.$store.dispatch("global/finishLoading");
                    }.bind(this)
                )
                .catch(
                    function(error) {
                        this.$store.dispatch("global/finishLoading");
                        this.$store.dispatch(
                            "global/showSnackbarError",
                            error.message
                        );
                    }.bind(this)
                );
        },
        handleData: function() {
            this.groupData = this.userGroups;
            this.groupData.forEach((obj, idx) => {
                obj.childData = [];
                obj.status = obj.deleted_at == null ? true : false;
                this.rawData.forEach((o, i) => {
                    if (obj.id == o.user_group_id) {
                        obj.childData.push(o);
                    }
                });
                obj.subAccounts = obj.childData.length;
            });
            this.filterData = this.groupData;
        },

        filterDataAction: function() {
            if (this.searchText != "") {
                this.filterData = this.groupData.filter(row => {
                    return (
                        !this.searchText ||
                        row.name
                            .toLowerCase()
                            .indexOf(this.searchText.toLowerCase()) > -1
                    );
                });
            } else {
                this.filterData = this.groupData;
            }
            this.setPages();
        },
        editItem: function(item, type) {
            this.editedIndex = this.filterData.indexOf(item);
            this.userInfo = Object.assign({}, item);
            if (type == 0) {
                this.dialog.edit = true;
            }
        },
        updateUserGroup: function() {
            if (this.$refs.editForm.validate()) {
                this.$store.dispatch("global/startLoading");
                if (this.editedIndex == -1) {
                    this.addNewUserGroup();
                } else {
                    this.$store.dispatch("global/startLoading");
                    this.$store
                        .dispatch("userGroup/updateUserGroup", this.userInfo)
                        .then(
                            function(result) {
                                this.$store.dispatch("global/finishLoading");
                                this.getAllUserGroups();
                                this.closeEditDialog();
                            }.bind(this)
                        )
                        .catch(
                            function(error) {
                                this.$store.dispatch("global/finishLoading");
                                this.$store.dispatch(
                                    "global/showSnackbarError",
                                    error.message
                                );
                            }.bind(this)
                        );
                }
            }
        },
        addItem: function() {
            this.$refs.editForm.reset();
            this.editedIndex = -1;
            this.dialog.edit = true;
        },
        addNewUserGroup: function() {
            var vm = this;
            if (this.$refs.editForm.validate()) {
                this.$store.dispatch("global/startLoading");
                this.$store
                    .dispatch("userGroup/newUserGroup", this.userInfo)
                    .then(
                        function(result) {
                            this.$store.dispatch(
                                "global/showSnackbarSuccess",
                                "Update user success!"
                            );
                            this.getAllUserGroups();
                            this.$store.dispatch("global/finishLoading");
                        }.bind(this)
                    )
                    .catch(
                        function(error) {
                            this.$store.dispatch(
                                "global/showSnackbarError",
                                error.message
                            );
                            this.$store.dispatch("global/finishLoading");
                        }.bind(this)
                    );
                this.closeEditDialog();
            }
        },
        closeEditDialog: function() {
            this.dialog.edit = false;
        },
        setPages: function() {
            if (this.perPage == null || this.filterData == null) {
                this.pages = null;
            } else {
                this.pages = Math.ceil(this.filterData.length / this.perPage);
            }
        }
    },
    computed: {
        formTitle() {
            return this.editedIndex === -1
                ? "New User Group"
                : "Edit User Group";
        }
    },
    watch: {
        searchText: function(value) {
            this.filterDataAction();
        },
        perPage: function(value) {
            this.pagination.rowsPerPage = value;
            this.setPages();
        }
    },
    mounted() {
        this.$router.push("/userGroup");
        this.getAllUserGroups();
    },
    created() {
        this.getAllUserGroups();
    }
};
